import React from 'react';
import './App.css';

class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            decimal : ''
        };

        this.convertRoman = this.convertRoman.bind(this);
    };

    convertRoman(e) {

        var romanMatrix = [
            [1000, 'M'],
            [900, 'CM'],
            [500, 'D'],
            [400, 'CD'],
            [100, 'C'],
            [90, 'XC'],
            [50, 'L'],
            [40, 'XL'],
            [10, 'X'],
            [9, 'IX'],
            [5, 'V'],
            [4, 'IV'],
            [1, 'I']
        ];

        console.log(e.target.value);

        if(isNaN(e.target.value)) {
            console.log('Not a number');
        } else {
            console.log('Number')
        }


        this.setState({ decimal : e.target.value });
    }

    render() {
        return (
            <div>
                <form>
                    <label>Decimal Number</label>
                    <input type="text" value={this.state.decimal} onChange={this.convertRoman}/>

                    <h1>{ this.state.decimal}</h1>
                </form>
            </div>
        );
    }
}

export default App;
