import React from 'react';
import './App.css';

class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            decimal : '',
            error : '',
            roman : ''
        };

        this.convertRoman = this.convertRoman.bind(this);
    };

    convertRoman(e) {

        if(isNaN(e.target.value)) {
            this.setState({ error : 'Please enter a valid number.' });
        } else {
            this.setState({ error : '' });

            if(e.target.value === 0) {
                this.setState({ error : 'Value must be in range 1 to 1000.' });
            } else {
                console.log(DecimalToRoman(e.target.value));
            }
        }

        this.setState({ decimal : e.target.value });
    }

    render() {
        return (
            <div>
                <form>
                    <label>Decimal Number</label>
                    <input type="text" value={this.state.decimal} onChange={this.convertRoman}/>

                    <h1>{ this.state.decimal}</h1>
                    <h4> { this.state.error}</h4>
                </form>
            </div>
        );
    }
}

function DecimalToRoman(num) {

    var romanMatrix = [
        [1000, 'M'],
        [900, 'CM'],
        [500, 'D'],
        [400, 'CD'],
        [100, 'C'],
        [90, 'XC'],
        [50, 'L'],
        [40, 'XL'],
        [10, 'X'],
        [9, 'IX'],
        [5, 'V'],
        [4, 'IV'],
        [1, 'I']
    ];

    for (var i = 0; i < romanMatrix.length; i++) {
        if (num >= romanMatrix[i][0]) {
            console.log(romanMatrix[i][1]);
            return romanMatrix[i][1] + DecimalToRoman(num - romanMatrix[i][0]);
        }
    }
}

export default App;
